<?phpclass Db{	private static $instancia;	public static function getInstance(){		if(self::$instancia == null){			self::$instancia = new PDO("pgsql:host=".DB_HOST."; dbname=".DB_NAME, DB_USER, DB_PASSWORD);			self::$instancia->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);		}		return self::$instancia;	}}